import React from "react";
import { Route, Routes, useLocation, useNavigate } from "react-router-dom";
import BoardList from "../component/Board/BoardList";
import Sidebar from "../component/Sidebar";
import Profile from "./Profile";
const Home = ({ id }) => {
  const menus = [
    { name: "자유게시판", path: "/board/1" },
    { name: "비밀게시판", path: "/board/2" },
  ];
  const navigate = useNavigate();
  const location = useLocation();

  const tableClickHandler = () => {
    navigate(`${location.pathname}`);
  };
  return (
    <div style={{ textAlign: "center", height: "100vh", overflow: "hidden" }}>
      <Sidebar menus={menus} onClick={tableClickHandler} />
      <Routes>
        <Route
          exact
          path="/board/1"
          element={<BoardList id={id} menus={menus.name} />}
        />
        <Route
          exact
          path="/board/2"
          element={<BoardList id={id} menus={menus.name} />}
        />
        <Route exact path="/profile" element={<Profile />} />
      </Routes>
    </div>
  );
};

export default Home;
