import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import style from "./Board.module.css";
const BoardDetail = ({ post }) => {
  let { no } = useParams();
  let findItem = post.find(function (it) {
    return it.no == no;
  });
  const navigate = useNavigate();
  const backToList = () => {
    navigate(-1);
  };

  return (
    <div className={style.BoardDetail}>
      <div>
        <h2 style={{ height: "120px" }}>{findItem.title}</h2>
      </div>
      <div style={{ margin: "0 auto", marginLeft: "20%" }}>
        <ul>
          <li style={{ float: "left" }}>글번호: {findItem.no}</li>
          <li style={{ float: "left", marginLeft: "10px" }}>
            작성자 : {findItem.userId}
          </li>
        </ul>
      </div>
      <div>
        <h2>작성일자 : {findItem.userId}</h2>
      </div>
      <div>
        <span>{findItem.content}</span>
      </div>
      <button onClick={backToList}>목록으로 돌아가기</button>
    </div>
  );
};
export default BoardDetail;
